local Players=game:GetService("Players");local RunService=game:GetService("RunService");local VirtualUser=game:GetService("VirtualUser");local LocalPlayer=Players.LocalPlayer;local bots={};local targetPlayer;local hitboxSize=Vector3.new(30,30,30);local numBots=3;local botSpeed=40;local status;local gui
local function spawnFlyBot(pos)local model=Instance.new("Model");model.Name="FlyBot";model.Parent=workspace;local root=Instance.new("Part");root.Name="HumanoidRootPart";root.Size=Vector3.new(5,7,3);root.Color=Color3.new(1,.2,.2);root.Transparency=.1;root.CanCollide=false;root.Anchored=false;root.Position=pos+Vector3.new(0,10,0);root.Parent=model;local hum=Instance.new("Humanoid");hum.Parent=model;hum.PlatformStand=true;model.PrimaryPart=root;local bb=Instance.new("BillboardGui",root);bb.Size=UDim2.new(0,100,0,50);bb.StudsOffset=Vector3.new(0,4,0);bb.AlwaysOnTop=true;local lbl=Instance.new("TextLabel",bb);lbl.Size=UDim2.new(1,0,1,0);lbl.BackgroundTransparency=1;lbl.Text="ðŸ¤– BOT";lbl.TextColor3=Color3.new(1,1,1);lbl.TextScaled=true;local bp=Instance.new("BodyPosition");bp.MaxForce=Vector3.new(8e3,3e3,8e3);bp.D=800;bp.P=5000;bp.Parent=root;return{model=model,root=root,bp=bp}end
local function spawnBots()local char=LocalPlayer.Character;if not char or not char:FindFirstChild("HumanoidRootPart")then if status then status.Text="Spawn first!"end;return end;for i=1,numBots do local botData=spawnFlyBot(char.HumanoidRootPart.Position+Vector3.new(math.random(-8,8),5,math.random(-8,8)));bots[#bots+1]=botData end;if status then status.Text=numBots.." ANTI-VOID BOTS!"end end
local function clearBots()for _,botData in bots do if botData.model then botData.model:Destroy()end end;bots={}end
local function createGUI()gui=Instance.new("ScreenGui");gui.Name="FlyBots";gui.Parent=game.CoreGui;gui.ResetOnSpawn=false;local frame=Instance.new("Frame",gui);frame.Size=UDim2.new(0,300,0,250);frame.Position=UDim2.new(0.5,-150,0,20);frame.BackgroundColor3=Color3.new(0.1,0.1,0.2);frame.Active=true;frame.Draggable=true;local uc=Instance.new("UICorner",frame);uc.CornerRadius=UDim.new(0,15);local title=Instance.new("TextLabel",frame);title.Size=UDim2.new(1,0,0,40);title.BackgroundTransparency=1;title.Text="ðŸ¤– ANTI-VOID BOT SWARM";title.TextColor3=Color3.new(1,1,1);title.TextScaled=true;local nameBox=Instance.new("TextBox",frame);nameBox.Size=UDim2.new(1,-20,0,40);nameBox.Position=UDim2.new(0,10,0,50);nameBox.PlaceholderText="Target Name";nameBox.TextScaled=true;local nc=Instance.new("UICorner",nameBox);nc.CornerRadius=UDim.new(0,10);local spawnBtn=Instance.new("TextButton",frame);spawnBtn.Size=UDim2.new(1,-20,0,45);spawnBtn.Position=UDim2.new(0,10,0,100);spawnBtn.BackgroundColor3=Color3.new(.2,.8,.2);spawnBtn.Text="SPAWN BOTS";spawnBtn.TextScaled=true;local sc=Instance.new("UICorner",spawnBtn);sc.CornerRadius=UDim.new(0,12);local clearBtn=Instance.new("TextButton",frame);clearBtn.Size=UDim2.new(.48,-10,0,40);clearBtn.Position=UDim2.new(0,10,0,155);clearBtn.BackgroundColor3=Color3.new(.8,.2,.2);clearBtn.Text="CLEAR";clearBtn.TextScaled=true;local cc=Instance.new("UICorner",clearBtn);cc.CornerRadius=UDim.new(0,10);status=Instance.new("TextLabel",frame);status.Size=UDim2.new(.48,-10,0,40);status.Position=UDim2.new(.5,5,0,155);status.BackgroundTransparency=1;status.Text="Ready!";status.TextScaled=true;spawnBtn.MouseButton1Click:Connect(function()local name=nameBox.Text:lower();targetPlayer=nil;for _,p in Players:GetPlayers()do if p~=LocalPlayer and p.Name:lower()==name then targetPlayer=p;break end end;if not targetPlayer then status.Text="Not Found!";return end;clearBots();spawnBots();status.Text="Swarming!";end);clearBtn.MouseButton1Click:Connect(function()clearBots();status.Text="Cleared";end)end
local lastSpam=0;RunService.Heartbeat:Connect(function()if targetPlayer and targetPlayer.Character then local tRoot=targetPlayer.Character:FindFirstChild("HumanoidRootPart");if tRoot then tRoot.Size=hitboxSize;tRoot.Transparency=.3;tRoot.CanCollide=false;for _,botData in bots do if botData.root and botData.bp and tRoot then local targetPos=tRoot.Position+Vector3.new(math.random(-4,4),3,math.random(-4,4));local rayParams=RaycastParams.new();rayParams.FilterType=Enum.RaycastFilterType.Exclude;rayParams.FilterDescendantsInstances={botData.model};local ray=workspace:Raycast(botData.root.Position,Vector3.new(0,-100,0),rayParams);if ray then targetPos=Vector3.new(targetPos.X,ray.Position.Y+3,targetPos.Z)end;botData.bp.Position=targetPos end end;local myRoot=LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart");if myRoot and(myRoot.Position-tRoot.Position).Magnitude<40 and tick()-lastSpam>0.1 then VirtualUser:ClickButton1(Vector2.new());lastSpam=tick()end end end end);createGUI();print("ANTI-VOID BOTS LOADED!")
